// Clean n8n API service - replaces all direct API calls
import { toast } from 'react-toastify';
import { savePodcastDetails, savePayoutDetails } from './supabase';

// n8n Configuration
const N8N_BASE_URL = process.env.REACT_APP_N8N_BASE_URL || 'https://n8n-6s78.onrender.com/';
const USE_TEST_WEBHOOKS = process.env.REACT_APP_USE_TEST_WEBHOOKS !== 'false'; // Default to true for testing
const DEV_MODE = process.env.REACT_APP_DEV_MODE === 'true';
// Use mock mode only if explicitly enabled
const USE_MOCK_MODE = DEV_MODE;

console.log('üîß n8n API Configuration:', {
  N8N_BASE_URL,
  USE_TEST_WEBHOOKS,
  REACT_APP_DEV_MODE: process.env.REACT_APP_DEV_MODE,
  NODE_ENV: process.env.NODE_ENV,
  DEV_MODE,
  USE_MOCK_MODE
});

class N8nApiService {
  constructor() {
    this.baseURL = N8N_BASE_URL;
    this.useTestWebhooks = USE_TEST_WEBHOOKS;
  }

  async makeRequest(endpoint, data) {
    // Use production webhooks for all endpoints now
    const baseUrl = this.baseURL.endsWith('/') ? this.baseURL.slice(0, -1) : this.baseURL;
    const webhookType = 'webhook'; // Always use production webhooks
    const targetUrl = `${baseUrl}/${webhookType}/${endpoint}`;

    console.log(`üì° Making request to endpoint: ${endpoint}`, {
      USE_MOCK_MODE,
      DEV_MODE,
      n8nBaseUrl: baseUrl,
      webhookType,
      targetUrl,
      payload: data
    });

    // Log the exact data being sent for debugging
    console.log(`üîç Webhook payload for ${endpoint}:`, JSON.stringify(data, null, 2));

    // Use mock mode if explicitly enabled
    if (USE_MOCK_MODE) {
      console.log(`üß™ Using mock response for ${endpoint}`);
      return this.getMockResponse(endpoint, data);
    }

    try {
      console.log(`üåê Calling n8n PRODUCTION webhook: ${targetUrl}`);

      // Set timeout based on endpoint - longer for episode creation
      const isLongRunning = ['create-episodes', 'create-podcast'].includes(endpoint);
      const timeoutDuration = isLongRunning ? 180000 : 30000; // 3 minutes for long-running, 30s for others

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), timeoutDuration);

      const response = await fetch(targetUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          // Add origin header to help with CORS debugging
          'Origin': window.location.origin
        },
        body: JSON.stringify(data),
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`‚ùå HTTP ${response.status} error for ${endpoint}:`, errorText);
        console.error(`üì§ Request was made to: ${targetUrl}`);
        console.error(`üì¶ Request payload was:`, JSON.stringify(data, null, 2));

        // For 404 errors, fall back to mock (workflow not configured)  
        if (response.status === 404) {
          console.warn('üîÑ n8n workflow not found, falling back to mock response');
          toast.info('Using offline mode - n8n workflow not configured');
          return this.getMockResponse(endpoint, data);
        }

        // Only fall back to mock for server errors in dev mode
        if (DEV_MODE && response.status >= 500) {
          console.warn('Server error in dev mode, falling back to mock response');
          return this.getMockResponse(endpoint, data);
        }

        throw new Error(`HTTP ${response.status}: ${response.statusText} - ${errorText}`);
      }

      // Check if response has content before parsing JSON
      const responseText = await response.text();
      console.log(`üì• Raw response from ${endpoint}:`, responseText);

      if (!responseText || responseText.trim() === '') {
        console.warn(`‚ö†Ô∏è Empty response from ${endpoint}, falling back to mock`);
        toast.info('Webhook returned empty response - using offline mode');
        return this.getMockResponse(endpoint, data);
      }

      let result;
      try {
        result = JSON.parse(responseText);
        console.log(`‚úÖ Parsed JSON from ${endpoint}:`, result);
      } catch (parseError) {
        console.error(`‚ùå JSON parse error for ${endpoint}:`, parseError);
        console.error(`üìù Response text was:`, responseText);
        console.warn('üîÑ Falling back to mock response due to parse error');
        toast.info('Webhook returned invalid response - using offline mode');
        return this.getMockResponse(endpoint, data);
      }

      // Handle N8N array response format
      const finalResult = Array.isArray(result) ? result[0] : result;
      console.log(`Processed response:`, finalResult);

      // Handle different response formats from n8n workflow
      if (endpoint === 'youtube-sync') {
        // Check if it's already in the expected format
        if (finalResult.success && finalResult.channel && finalResult.videos) {
          console.log('‚úÖ Received properly formatted YouTube sync response');
          return finalResult;
        }

        // Handle raw YouTube API response format
        if (Array.isArray(finalResult) && !finalResult.success) {
          console.warn('‚ö†Ô∏è n8n returned raw YouTube data, transforming...');
          return this.transformYouTubeResponse(finalResult);
        }

        // Handle single object response format
        if (finalResult.channel || finalResult.videos || finalResult.items) {
          console.warn('‚ö†Ô∏è n8n returned partial data format, transforming...');
          return this.transformYouTubeResponse([finalResult]);
        }
      }

      return finalResult;
    } catch (error) {
      console.error(`n8n API error for ${endpoint}:`, error);

      // Fall back to mock for network/CORS failures (common in development)
      if (error.message.includes('Failed to fetch') || 
          error.message.includes('NetworkError') ||
          error.message.includes('CORS') ||
          error.name === 'AbortError' ||
          error.message.includes('fetch')) {
        console.warn('üîÑ Network/CORS error, falling back to mock response');
        toast.info('Using offline mode - n8n server not available or CORS issue');
        return this.getMockResponse(endpoint, data);
      }

      // Better error messages
      if (error.message.includes('HTTP 404')) {
        throw new Error('Workflow not found. Please ensure n8n workflows are properly set up.');
      } else if (error.message.includes('HTTP 500')) {
        throw new Error('Server error occurred. Please try again in a moment.');
      } else {
        throw new Error(`n8n workflow failed: ${error.message}`);
      }
    }
  }

