import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../App';
import { toast } from 'react-toastify';
import { getUserPodcasts } from '../services/supabase';

// YouTube OAuth scopes
const YOUTUBE_SCOPES = [
  'https://www.googleapis.com/auth/youtube.readonly',
  'https://www.googleapis.com/auth/userinfo.profile', 
  'https://www.googleapis.com/auth/userinfo.email'
].join(' ');

const CLIENT_ID = '678018511336-jgd34uakj7vika3m6nvf028ekcfu2ucf.apps.googleusercontent.com';

function startGoogleOAuth() {
  const redirectUri = window.location.origin + '/auth/callback';
  const state = Math.random().toString(36).substring(2, 15);
  localStorage.setItem('oauth_state', state);

  const url = `https://accounts.google.com/o/oauth2/v2/auth?` +
    `client_id=${CLIENT_ID}&` +
    `redirect_uri=${encodeURIComponent(redirectUri)}&` +
    `response_type=token&` +
    `scope=${encodeURIComponent(YOUTUBE_SCOPES)}&` +
    `include_granted_scopes=true&` +
    `state=${state}&` +
    `prompt=consent`;

  window.location.href = url;
}

function extractTokenFromUrl() {
  const hash = window.location.hash;
  console.log('üîç Checking URL hash:', hash);

  if (!hash || !hash.includes('access_token')) {
    return null;
  }

  const params = new URLSearchParams(hash.substring(1));
  const accessToken = params.get('access_token');
  const state = params.get('state');
  const storedState = localStorage.getItem('oauth_state');

  if (state && state === storedState) {
    localStorage.removeItem('oauth_state');
    return { accessToken };
  }

  return null;
}

export default function GoogleAuthSimple() {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [loading, setLoading] = useState(false);
  const [authStep, setAuthStep] = useState('checking');

  useEffect(() => {
    const handleAuth = async () => {
      console.log('üîç Simple auth check...');

      // 1. Check for OAuth token in URL
      const tokenData = extractTokenFromUrl();
      if (tokenData) {
        console.log('‚úÖ Found OAuth token, processing...');
        window.history.replaceState(null, null, '/auth');
        await processAuth(tokenData.accessToken);
        return;
      }

      // 2. Check if already signed in
      const token = localStorage.getItem('google_token');
      const userData = localStorage.getItem('user_info');

      if (token && userData) {
        console.log('‚úÖ Already signed in, checking status...');
        const user = JSON.parse(userData);
        login();

        try {
          const podcasts = await getUserPodcasts(user.sub);
          if (podcasts && podcasts.length > 0) {
            console.log('‚úÖ Has podcasts ‚Üí dashboard');
            navigate('/dashboard');
          } else {
            console.log('üìù No podcasts ‚Üí onboarding');
            navigate('/create-podcast');
          }
        } catch (error) {
          console.error('Error checking podcasts:', error);
          setAuthStep('idle');
        }
      } else {
        console.log('üìù Not signed in, showing login');
        setAuthStep('idle');
      }
    };

    handleAuth();
  }, [navigate, login]);

  const processAuth = async (accessToken) => {
    setLoading(true);

    try {
      // Store token
      localStorage.setItem('google_token', accessToken);

      // Get user info
      const response = await fetch(`https://www.googleapis.com/oauth2/v2/userinfo?access_token=${accessToken}`);
      const userData = await response.json();

      localStorage.setItem('user_info', JSON.stringify(userData));
      login();

      console.log(`üîç Checking podcasts for ${userData.name}...`);

      // Check if user has podcasts
      const podcasts = await getUserPodcasts(userData.sub);

      if (podcasts && podcasts.length > 0) {
        console.log('‚úÖ Has podcasts ‚Üí dashboard');
        toast.success(`Welcome back, ${userData.name}!`);
        navigate('/dashboard');
      } else {
        console.log('üìù No podcasts ‚Üí onboarding');
        toast.success(`Welcome, ${userData.name}! Let's create your podcast.`);
        navigate('/create-podcast');
      }

    } catch (error) {
      console.error('Auth error:', error);
      toast.error('Authentication failed');
      setAuthStep('idle');
    } finally {
      setLoading(false);
    }
  };

  if (authStep === 'checking' || loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-500 mx-auto mb-4"></div>
          <p className="text-gray-600">
            {loading ? 'Signing you in...' : 'Checking authentication...'}
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <h2 className="mt-6 text-3xl font-extrabold text-gray-900">
            Welcome to PodPay
          </h2>
          <p className="mt-2 text-sm text-gray-600">
            Sign in with Google to create your podcast
          </p>
        </div>

        <div className="mt-8">
          <button
            onClick={startGoogleOAuth}
            disabled={loading}
            className="group relative w-full flex justify-center py-3 px-4 border border-transparent text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
          >
            <svg className="w-5 h-5 mr-2" viewBox="0 0 24 24">
              <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
              <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
              <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
              <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
            </svg>
            Continue with Google
          </button>
        </div>
      </div>
    </div>
  );
}
