import React, { useState, createContext, useContext, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import GoogleAuth from './components/GoogleAuth';
import Dashboard from './components/Dashboard';
import PodcastCreationFlow from './components/PodcastCreationFlow';
import PodcastDetails from './components/PodcastDetails';
import AnalyticsPage from './components/AnalyticsPage';
import Podcast from './components/Podcast';
import TransactionsPage from './components/TransactionsPage';
import ReferralsPage from './components/ReferralsPage';
import SettingsPage from './components/SettingsPage';
import { useYouTubeSync } from './hooks/useYouTubeSync';
import { toast } from 'react-toastify';
import { isOnboardingCompleted, clearOnboardingData } from './utils/onboarding';

// Auth context
const AuthContext = createContext();
export const useAuth = () => useContext(AuthContext);


function PodcastProtectedRoute({ children }) {
  const { signedIn, userInfo } = useAuth();
  const [onboardingCheck, setOnboardingCheck] = React.useState('checking');

  React.useEffect(() => {
    if (!signedIn || !userInfo) {
      setOnboardingCheck('not-signed-in');
      return;
    }

    // Check if onboarding is completed
    const checkOnboarding = async () => {
      try {
        // First check localStorage for quick determination
        if (isOnboardingCompleted()) {
          setOnboardingCheck('completed');
          return;
        }

        // If localStorage check fails, check if user has podcasts in Supabase
        const { getUserPodcasts } = await import('./services/supabase');
        const userPodcasts = await getUserPodcasts(userInfo.sub);

        if (userPodcasts && userPodcasts.length > 0) {
          // User has podcasts, mark onboarding as complete
          console.log('‚úÖ User has existing podcasts, marking onboarding complete');
          setOnboardingCheck('completed');

          // Store the first podcast as current
          localStorage.setItem('currentPodcast', JSON.stringify(userPodcasts[0]));
          localStorage.setItem('onboardingCompleted', 'true');
        } else {
          setOnboardingCheck('incomplete');
        }
      } catch (error) {
        console.error('‚ùå Error checking onboarding status:', error);
        setOnboardingCheck('incomplete');
      }
    };

    checkOnboarding();
  }, [signedIn, userInfo]);

  if (!signedIn) {
    return <Navigate to="/auth" replace />;
  }

  if (onboardingCheck === 'checking') {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  if (onboardingCheck === 'incomplete') {
    return <Navigate to="/auth" replace />;
  }

  return children;
}

function App() {
  const [signedIn, setSignedIn] = useState(false);
  const [userInfo, setUserInfo] = useState(null);

  // Check for existing session on mount
  useEffect(() => {
    const token = localStorage.getItem('google_token');
    const userData = localStorage.getItem('user_info');
    const podcastId = localStorage.getItem('podcastId');
    const onboardingCompleted = localStorage.getItem('onboardingCompleted');

    console.log('üîç App initialization - checking stored auth state:', {
      hasToken: !!token,
      hasUserData: !!userData,
      hasPodcastId: !!podcastId,
      onboardingCompleted
    });

    if (token && userData) {
      setSignedIn(true);
      try {
        setUserInfo(JSON.parse(userData));
        console.log('‚úÖ Restored user session from localStorage');
      } catch (error) {
        console.error('Error parsing user data:', error);
        // Clear invalid data
        localStorage.removeItem('user_info');
        setSignedIn(false);
      }
    }
  }, []);

  const login = () => setSignedIn(true);

  const logout = () => {
    // Clear auth data
    localStorage.removeItem('google_token');
    localStorage.removeItem('user_info');
    localStorage.removeItem('isYouTubeConnected');

    // Clear onboarding data
    clearOnboardingData();

    setSignedIn(false);
    setUserInfo(null);

    toast.info('You have been signed out');
  };

  // Use the sync hook at the top level to share state
  const sync = useYouTubeSync();

  // Auth callback handler for OAuth redirect
  function AuthCallbackHandler() {
    return <GoogleAuth />;
  }

  return (
    <AuthContext.Provider value={{ signedIn, login, logout, userInfo }}>
      <Router>
        <Routes>
          {/* Main authentication route */}
          <Route path="/auth" element={
            signedIn ? (
              // If signed in, check if onboarding is complete
              isOnboardingCompleted() ? 
                <Navigate to="/dashboard" replace /> : 
                <GoogleAuth />
            ) : <GoogleAuth />
          } />
          <Route path="/auth/callback" element={<AuthCallbackHandler />} />

          {/* Podcast creation form route - only for users without existing podcasts */}
          <Route path="/create-podcast" element={
            signedIn ? <PodcastCreationFlow /> : <Navigate to="/auth" replace />
          } />

          {/* Protected dashboard route */}
          <Route path="/dashboard" element={
            <PodcastProtectedRoute>
              <Dashboard sync={sync} userInfo={userInfo} />
            </PodcastProtectedRoute>
          } />

          {/* Podcast details route */}
          <Route path="/podcast" element={
            <PodcastProtectedRoute>
              <Podcast userInfo={userInfo} />
            </PodcastProtectedRoute>
          } />

          {/* Analytics route */}
          <Route path="/analytics" element={
            <PodcastProtectedRoute>
              <AnalyticsPage userInfo={userInfo} />
            </PodcastProtectedRoute>
          } />

          {/* Transactions route */}
          <Route path="/transactions" element={
            <PodcastProtectedRoute>
              <TransactionsPage userInfo={userInfo} />
            </PodcastProtectedRoute>
          } />

          {/* Referrals route */}
          <Route path="/referrals" element={
            <PodcastProtectedRoute>
              <ReferralsPage userInfo={userInfo} />
            </PodcastProtectedRoute>
          } />

          {/* Settings route */}
          <Route path="/settings" element={
            <PodcastProtectedRoute>
              <SettingsPage userInfo={userInfo} />
            </PodcastProtectedRoute>
          } />

          {/* Default route - redirect based on auth status */}
          <Route path="/" element={
            <Navigate to={signedIn ? "/dashboard" : "/auth"} replace />
          } />

          {/* Legacy routes - redirect to new auth flow */}
          <Route path="/login" element={<Navigate to="/auth" replace />} />
          <Route path="/signup" element={<Navigate to="/auth" replace />} />
          <Route path="/connect-youtube" element={<Navigate to="/auth" replace />} />
        </Routes>
      </Router>

      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
        style={{ zIndex: 9999 }}
      />
    </AuthContext.Provider>
  );
}

export default App;
