import React from 'react';
import PodPayLayout from './PodPayLayout';
import { usePodcastData } from '../hooks/usePodcastData';
import { useEpisodesData } from '../hooks/useEpisodesData';
import { 
  ChevronRight,
  MoreHorizontal,
  Calendar,
  FileText,
  Play,
  Eye,
  Edit,
  Trash2
} from 'lucide-react';

interface UserInfo {
  sub: string;
  email?: string;
  name?: string;
  given_name?: string;
  family_name?: string;
}

interface PodcastProps {
  userInfo: UserInfo | null;
}

export default function Podcast({ userInfo }: PodcastProps) {
  const [activeTab, setActiveTab] = React.useState('episodes');
  const { podcasts, currentPodcast, loading, error } = usePodcastData(userInfo);
  const { episodes: realEpisodes, loading: episodesLoading, error: episodesError } = useEpisodesData(currentPodcast?.id || null);

  // Mock episodes data - this will be replaced with real data later
  const mockEpisodes = [
    {
      id: 1,
      title: "Episode 1: Introduction to Our Podcast",
      status: "Published",
      publishedDate: "2024-01-15",
      format: "MP3",
      preRolls: 1,
      midRolls: 2,
      postRolls: 1,
      duration: "45:32"
    },
    {
      id: 2,
      title: "Episode 2: Deep Dive into AI Technology",
      status: "Draft",
      publishedDate: "2024-01-22",
      format: "MP3",
      preRolls: 1,
      midRolls: 3,
      postRolls: 1,
      duration: "52:18"
    },
    {
      id: 3,
      title: "Episode 3: Future of Content Creation",
      status: "Published",
      publishedDate: "2024-01-29",
      format: "MP3",
      preRolls: 2,
      midRolls: 2,
      postRolls: 1,
      duration: "38:45"
    }
  ];

  if (loading) {
    return (
      <PodPayLayout
        activeMenuItem="podcast"
        headerTitle="Podcast"
        userInfo={userInfo}
      >
        <div className="p-8 flex items-center justify-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        </div>
      </PodPayLayout>
    );
  }

  if (error) {
    return (
      <PodPayLayout
        activeMenuItem="podcast"
        headerTitle="Podcast"
        userInfo={userInfo}
      >
        <div className="p-8">
          <div className="podpay-card border-l-4 border-l-red-500 bg-red-50">
            <p className="text-red-800">Error loading podcast: {error}</p>
          </div>
        </div>
      </PodPayLayout>
    );
  }

  return (
    <PodPayLayout
      activeMenuItem="podcast"
      headerTitle={currentPodcast?.title || "Podcast"}
      headerSubtitle="Podcasts"
      userInfo={userInfo}
    >
      <div className="p-8 space-y-6">
        {/* Breadcrumb */}
        <div className="flex items-center space-x-2 text-sm text-gray-500">
          <span>Home</span>
          <ChevronRight className="h-4 w-4" />
          <span>Podcasts</span>
          <ChevronRight className="h-4 w-4" />
          <span className="text-gray-900 font-medium">
            {currentPodcast?.title || "Select a Podcast"}
          </span>
        </div>

        {currentPodcast ? (
          <>
            {/* Podcast Header */}
            <div className="podpay-card">
              <div className="flex items-start space-x-6">
                {/* Podcast Artwork */}
                <div className="flex-shrink-0">
                  <img 
                    src={currentPodcast.image_url || "/placeholder-podcast.jpg"} 
                    alt={currentPodcast.title}
                    className="w-32 h-32 rounded-lg object-cover shadow-lg"
                  />
                </div>

                {/* Podcast Info */}
                <div className="flex-1">
                  <div className="flex items-start justify-between">
                    <div>
                      <h1 className="text-3xl font-bold text-gray-900 mb-2">
                        {currentPodcast.title}
                      </h1>

                      {/* Category Tags */}
                      <div className="flex items-center space-x-2 mb-3">
                        {currentPodcast.category && (
                          <span className="inline-flex items-center px-3 py-1 rounded-full text-sm bg-primary/10 text-primary border border-primary/20">
                            {currentPodcast.category}
                          </span>
                        )}
                        <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium ${
                          currentPodcast.status === 'active' 
                            ? 'bg-green-100 text-green-800 border border-green-200' 
                            : 'bg-gray-100 text-gray-800 border border-gray-200'
                        }`}>
                          {currentPodcast.status}
                        </span>
                      </div>

                      {/* Description */}
                      <p className="text-gray-600 mb-4 leading-relaxed">
                        {currentPodcast.description || currentPodcast.subtitle || "No description available"}
                      </p>

                      {/* Metadata */}
                      <div className="flex items-center space-x-6 text-sm text-gray-500">
                        <span>{currentPodcast.episodes_count || 0} episodes</span>
                        <span>•</span>
                        <span>{currentPodcast.language || 'English'}</span>
                        <span>•</span>
                        <span>Created {new Date(currentPodcast.created_at).toLocaleDateString()}</span>
                      </div>
                    </div>

                    {/* Action Button */}
                    <button className="text-gray-400 hover:text-gray-600 p-2">
                      <MoreHorizontal className="h-5 w-5" />
                    </button>
                  </div>
                </div>
              </div>
            </div>

            {/* Tabs Navigation */}
            <div className="border-b border-gray-200">
              <nav className="-mb-px flex space-x-8">
                {[
                  { id: 'episodes', label: 'Episodes', count: mockEpisodes.length },
                  { id: 'drafts', label: 'Drafts', count: 2 },
                  { id: 'clips', label: 'Clips', count: 0 },
                  { id: 'analytics', label: 'Analytics' }
                ].map((tab) => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`py-3 px-1 border-b-2 font-medium text-sm relative ${
                      activeTab === tab.id
                        ? 'border-primary text-primary'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                    }`}
                  >
                    {tab.label}
                    {tab.count !== undefined && (
                      <span className="ml-2 inline-flex items-center px-2 py-0.5 rounded-full text-xs bg-gray-100 text-gray-600">
                        {tab.count}
                      </span>
                    )}
                  </button>
                ))}
              </nav>
            </div>

            {/* Tab Content */}
            {activeTab === 'episodes' && (
              <div className="podpay-card">
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-lg font-semibold text-gray-900">Episodes</h2>
                  <button className="podpay-btn-primary">
                    Add Episode
                  </button>
                </div>

                {/* Episodes Table */}
                <div className="overflow-x-auto">
                  <table className="podpay-table">
                    <thead>
                      <tr>
                        <th>Episode</th>
                        <th>Status</th>
                        <th>Published Date</th>
                        <th>Format</th>
                        <th>Pre-rolls</th>
                        <th>Mid-rolls</th>
                        <th>Post-rolls</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {mockEpisodes.map((episode) => (
                        <tr key={episode.id}>
                          <td>
                            <div className="flex items-center space-x-3">
                              <div className="flex-shrink-0">
                                <div className="w-10 h-10 bg-gray-100 rounded-lg flex items-center justify-center">
                                  <Play className="h-4 w-4 text-gray-500" />
                                </div>
                              </div>
                              <div>
                                <div className="font-medium text-gray-900">{episode.title}</div>
                                <div className="text-sm text-gray-500">{episode.duration}</div>
                              </div>
                            </div>
                          </td>
                          <td>
                            <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                              episode.status === 'Published' 
                                ? 'bg-green-100 text-green-800' 
                                : 'bg-yellow-100 text-yellow-800'
                            }`}>
                              {episode.status}
                            </span>
                          </td>
                          <td className="text-gray-900">
                            {new Date(episode.publishedDate).toLocaleDateString()}
                          </td>
                          <td className="text-gray-900">{episode.format}</td>
                          <td className="text-gray-900">{episode.preRolls}</td>
                          <td className="text-gray-900">{episode.midRolls}</td>
                          <td className="text-gray-900">{episode.postRolls}</td>
                          <td>
                            <div className="flex items-center space-x-2">
                              <button className="text-gray-400 hover:text-gray-600 p-1">
                                <Eye className="h-4 w-4" />
                              </button>
                              <button className="text-gray-400 hover:text-gray-600 p-1">
                                <Edit className="h-4 w-4" />
                              </button>
                              <button className="text-gray-400 hover:text-red-600 p-1">
                                <Trash2 className="h-4 w-4" />
                              </button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            )}

            {activeTab === 'drafts' && (
              <div className="podpay-card">
                <h2 className="text-lg font-semibold text-gray-900 mb-4">Draft Episodes</h2>
                <div className="text-center py-12 text-gray-500">
                  <FileText className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                  <p>No draft episodes yet</p>
                  <p className="text-sm">Create your first draft episode to get started</p>
                </div>
              </div>
            )}

            {activeTab === 'clips' && (
              <div className="podpay-card">
                <h2 className="text-lg font-semibold text-gray-900 mb-4">Podcast Clips</h2>
                <div className="text-center py-12 text-gray-500">
                  <Calendar className="h-12 w-12 mx-auto mb-4 text-gray-300" />
                  <p>No clips available</p>
                  <p className="text-sm">Create clips from your episodes to share on social media</p>
                </div>
              </div>
            )}

            {activeTab === 'analytics' && (
              <div className="podpay-card">
                <h2 className="text-lg font-semibold text-gray-900 mb-4">Analytics</h2>
                <div className="text-center py-12 text-gray-500">
                  <div className="h-12 w-12 mx-auto mb-4 text-gray-300">📊</div>
                  <p>Analytics data will appear here</p>
                  <p className="text-sm">Track your podcast performance and audience insights</p>
                </div>
              </div>
            )}
          </>
        ) : (
          <div className="podpay-card text-center py-12">
            <div className="h-16 w-16 mx-auto mb-4 bg-gray-100 rounded-lg flex items-center justify-center">
              <FileText className="h-8 w-8 text-gray-400" />
            </div>
            <h3 className="text-lg font-medium text-gray-900 mb-2">No Podcast Selected</h3>
            <p className="text-gray-500 mb-4">Create your first podcast to get started</p>
            <button className="podpay-btn-primary">
              Create Podcast
            </button>
          </div>
        )}
      </div>
    </PodPayLayout>
  );
}
