import React, { useState, useEffect } from 'react';
import PodPayLayout from './PodPayLayout';
import { usePodcastData } from '../hooks/usePodcastData';
import { useYouTubeSync } from '../hooks/useYouTubeSync';
import { useEpisodes } from '../hooks/useEpisodes';
import { 
  MoreHorizontal,
  Play,
  Eye,
  Edit,
  Trash2,
  ExternalLink
} from 'lucide-react';

export default function PodcastPage({ userInfo }) {
  const { podcasts, currentPodcast, loading, error } = usePodcastData(userInfo);
  const sync = useYouTubeSync();
  // Temporarily disable episode fetching until n8n workflow is set up
  // const megaphoneEpisodes = useEpisodes(currentPodcast?.megaphone_id);
  const megaphoneEpisodes = { loading: false, error: null, episodes: [] };

  // Mock episodes data that matches the screenshot
  const mockEpisodes = [
    {
      id: 1,
      title: "Lorem Ipsum",
      status: "Schedule",
      publishedDate: "Jan 6, 2025",
      format: "Audio",
      preRolls: 1,
      midRolls: 1,
      postRolls: 1
    },
    {
      id: 2,
      title: "Lorem Ipsum",
      status: "Schedule", 
      publishedDate: "Jan 6, 2025",
      format: "Audio",
      preRolls: 1,
      midRolls: 1,
      postRolls: 1
    },
    {
      id: 3,
      title: "Lorem Ipsum",
      status: "Schedule",
      publishedDate: "Jan 6, 2025", 
      format: "Audio",
      preRolls: 1,
      midRolls: 1,
      postRolls: 1
    },
    {
      id: 4,
      title: "Lorem Ipsum",
      status: "Schedule",
      publishedDate: "Jan 6, 2025",
      format: "Audio", 
      preRolls: 1,
      midRolls: 1,
      postRolls: 1
    }
  ];

  // Transform YouTube sync episodes (episodes being created from YouTube videos)
  const youtubeEpisodes = sync.episodes?.map(episode => ({
    id: `youtube_${episode.id}`,
    title: episode.title || "Untitled Episode",
    status: episode.publishedToMegaphone ? "Published" : (episode.error ? "Failed" : "Processing"),
    publishedDate: episode.publishedAt ? new Date(episode.publishedAt).toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    }) : "Unknown",
    format: "Audio",
    preRolls: 1,
    midRolls: 1,
    postRolls: 1,
    youtubeUrl: episode.mp4Url,
    error: episode.error,
    source: 'youtube'
  })) || [];

  // Transform Megaphone episodes (published episodes from Megaphone)
  const publishedEpisodes = megaphoneEpisodes.episodes?.map(episode => ({
    id: `megaphone_${episode.id}`,
    title: episode.title || "Untitled Episode",
    status: episode.status === 'published' ? "Published" : "Draft",
    publishedDate: episode.publishedAt ? new Date(episode.publishedAt).toLocaleDateString('en-US', { 
      year: 'numeric', 
      month: 'short', 
      day: 'numeric' 
    }) : "Unknown",
    format: "Audio",
    preRolls: 1,
    midRolls: 1,
    postRolls: 1,
    duration: episode.duration ? `${Math.floor(episode.duration / 60)}:${String(episode.duration % 60).padStart(2, '0')}` : "Unknown",
    plays: episode.playCount || 0,
    downloads: episode.downloadCount || 0,
    source: 'megaphone'
  })) || [];

  // Combine all episode sources, prioritizing published episodes first
  const allRealEpisodes = [...publishedEpisodes, ...youtubeEpisodes];

  // Use real episodes if available, otherwise show mock data
  const displayEpisodes = allRealEpisodes.length > 0 ? allRealEpisodes : mockEpisodes;

  if (loading || megaphoneEpisodes.loading) {
    return (
      <PodPayLayout
        activeMenuItem="podcast"
        headerTitle="Podcast"
        userInfo={userInfo}
      >
        <div className="p-8 flex items-center justify-center min-h-96">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        </div>
      </PodPayLayout>
    );
  }

  if (error) {
    return (
      <PodPayLayout
        activeMenuItem="podcast"
        headerTitle="Podcast"
        userInfo={userInfo}
      >
        <div className="p-8">
          <div className="podpay-card border-l-4 border-l-red-500 bg-red-50">
            <p className="text-red-800">Error loading podcast: {error}</p>
            {megaphoneEpisodes.error && (
              <p className="text-red-700 mt-2">Episode loading error: {megaphoneEpisodes.error}</p>
            )}
          </div>
        </div>
      </PodPayLayout>
    );
  }

  // Use real podcast data with proper fallbacks
  const podcast = currentPodcast ? {
    ...currentPodcast,
    // Provide fallbacks for missing data
    summary: currentPodcast.summary || `Welcome to ${currentPodcast.title || 'this podcast'}! This is an auto-generated description. You can update this in your podcast settings to provide more details about what listeners can expect from your show.`,
    subtitle: currentPodcast.subtitle || `${currentPodcast.title || 'Podcast'} - ${currentPodcast.author || 'Your Name'}`,
    primary_category: currentPodcast.primary_category || "Technology"
  } : {
    title: "Listen to the people's voice",
    image_url: "/api/placeholder/300/300",
    primary_category: "Audio",
    summary: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec sit amet tincidunt tellus. Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia curae; Suspendisse euismod ornare urna eu mollis. Cras vel diam ac nibh accumsan ornare quis quis nisl.\n\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Donec sit amet tincidunt tellus. Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia curae; Suspendisse euismod ornare urna eu mollis. Cras vel diam ac nibh accumsan ornare quis quis nisl.",
    managedBy: "https://podcastpayouts.com/"
  };

  // Get description text - use summary if available, otherwise fallback
  const podcastDescription = podcast.summary || podcast.subtitle || "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec sit amet tincidunt tellus. Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia curae; Suspendisse euismod ornare urna eu mollis. Cras vel diam ac nibh accumsan ornare quis quis nisl.\n\nLorem ipsum dolor sit amet, consectetur adipiscing elit. Donec sit amet tincidunt tellus. Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia curae; Suspendisse euismod ornare urna eu mollis. Cras vel diam ac nibh accumsan ornare quis quis nisl.";

  // Get categories - real categories plus some defaults for the design
  const categories = [
    podcast.primary_category || "Audio",
    podcast.secondary_category,
    "History", // Keep from design
    "Documentary", // Keep from design  
    "Society & Culture" // Keep from design
  ].filter(Boolean); // Remove null/undefined values

  return (
    <PodPayLayout
      activeMenuItem="podcast"
      headerTitle="Podcast Details"
      headerSubtitle="Podcast"
      userInfo={userInfo}
    >
      <div className="p-8 space-y-8">
        {/* Podcast Header */}
        <div className="flex items-start space-x-8">
          {/* Podcast Artwork */}
          <div className="flex-shrink-0">
            <img 
              src={podcast.image_url && podcast.image_url !== "https://megaphone.imgix.net/?ixlib=rails-4.3.1" 
                ? podcast.image_url 
                : (podcast.megaphone_image_url && podcast.megaphone_image_url !== "https://megaphone.imgix.net/?ixlib=rails-4.3.1"
                  ? podcast.megaphone_image_url 
                  : "data:image/svg+xml;base64,")} 
              alt={podcast.title}
              className="w-48 h-48 rounded-2xl object-cover shadow-lg"
              onError={(e) => {
                e.target.src = "data:image/svg+xml;base64,";
              }}
            />
          </div>

          {/* Podcast Info */}
          <div className="flex-1">
            <h1 className="text-4xl font-bold text-gray-900 mb-4">
              {podcast.title}
            </h1>

            {/* Category Tags */}
            <div className="flex items-center space-x-3 mb-6 flex-wrap">
              {categories.map((category, index) => (
                <span key={index} className="inline-flex items-center px-4 py-2 rounded-full text-sm bg-gray-100 text-gray-700 border">
                  {category}
                </span>
              ))}
              {/* Show additional metadata as tags */}
              {podcast.language && (
                <span className="inline-flex items-center px-4 py-2 rounded-full text-sm bg-blue-100 text-blue-700 border border-blue-200">
                  {podcast.language.toUpperCase()}
                </span>
              )}
              {podcast.explicit && (
                <span className="inline-flex items-center px-4 py-2 rounded-full text-sm bg-red-100 text-red-700 border border-red-200">
                  Explicit
                </span>
              )}
              {podcast.podcast_type && (
                <span className="inline-flex items-center px-4 py-2 rounded-full text-sm bg-green-100 text-green-700 border border-green-200">
                  {podcast.podcast_type}
                </span>
              )}
            </div>

            {/* Description */}
            <div className="space-y-4 text-gray-600 leading-relaxed max-w-4xl">
              {podcastDescription.split('\n\n').map((paragraph, index) => (
                <p key={index}>
                  {paragraph}
                </p>
              ))}
            </div>

            {/* Additional Podcast Metadata */}
            {currentPodcast && (
              <div className="mt-6 grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                <div>
                  <span className="text-gray-500 block">Author</span>
                  <span className="font-medium">{podcast.author || 'Unknown'}</span>
                </div>
                <div>
                  <span className="text-gray-500 block">Episodes</span>
                  <span className="font-medium">{displayEpisodes.length}</span>
                </div>
                <div>
                  <span className="text-gray-500 block">Status</span>
                  <span className={`font-medium ${podcast.status === 'active' ? 'text-green-600' : 'text-gray-600'}`}>
                    {podcast.status || 'Unknown'}
                  </span>
                </div>
                <div>
                  <span className="text-gray-500 block">Created</span>
                  <span className="font-medium">
                    {podcast.created_at ? new Date(podcast.created_at).toLocaleDateString() : 'Unknown'}
                  </span>
                </div>
              </div>
            )}

            {/* Managed By & RSS Feed */}
            <div className="mt-6 flex flex-col space-y-3">
              <div className="flex items-center space-x-2">
                <span className="text-gray-500 italic">Managed by</span>
                <a 
                  href="https://podcastpayouts.com/" 
                  target="_blank" 
                  rel="noopener noreferrer"
                  className="text-blue-600 hover:text-blue-700 border border-blue-200 px-3 py-1 rounded-lg text-sm font-medium hover:bg-blue-50 transition-colors"
                >
                  https://podcastpayouts.com/
                </a>
              </div>

              {/* RSS Feed Link */}
              {podcast.feed_url && (
                <div className="flex items-center space-x-2">
                  <span className="text-gray-500 italic">RSS Feed</span>
                  <a 
                    href={podcast.feed_url} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-green-600 hover:text-green-700 border border-green-200 px-3 py-1 rounded-lg text-sm font-medium hover:bg-green-50 transition-colors"
                  >
                    View RSS Feed
                  </a>
                </div>
              )}

              {/* YouTube Channel Link */}
              {podcast.link && (
                <div className="flex items-center space-x-2">
                  <span className="text-gray-500 italic">YouTube Channel</span>
                  <a 
                    href={podcast.link} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-red-600 hover:text-red-700 border border-red-200 px-3 py-1 rounded-lg text-sm font-medium hover:bg-red-50 transition-colors"
                  >
                    View Channel
                  </a>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Episodes Section */}
        <div className="space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-2xl font-bold text-gray-900">
              Episodes <span className="text-gray-500 font-normal">({displayEpisodes.length})</span>
            </h2>
          </div>

          {/* Episodes Table */}
          <div className="bg-white rounded-lg shadow-sm border overflow-hidden">
            <table className="w-full">
              <thead className="bg-gray-50 border-b">
                <tr>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    <div className="flex items-center space-x-2">
                      <input type="checkbox" className="w-4 h-4 text-blue-600 border-gray-300 rounded" />
                      <span>Episodes</span>
                    </div>
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Published Date
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Format
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Pre-rolls
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Mid-rolls
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Post-rolls
                  </th>
                  <th className="px-6 py-4 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Action
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {displayEpisodes.map((episode, index) => (
                  <tr key={episode.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center space-x-3">
                        <input type="checkbox" className="w-4 h-4 text-blue-600 border-gray-300 rounded" />
                        <div className="flex items-center space-x-2">
                          <span className="text-sm font-medium text-gray-900">{episode.title}</span>
                          {episode.youtubeUrl && (
                            <a 
                              href={episode.youtubeUrl} 
                              target="_blank" 
                              rel="noopener noreferrer"
                              className="text-red-600 hover:text-red-700"
                            >
                              <ExternalLink className="h-3 w-3" />
                            </a>
                          )}
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                        episode.status === 'Published' ? 'bg-green-100 text-green-800' :
                        episode.status === 'Failed' ? 'bg-red-100 text-red-800' :
                        episode.status === 'Processing' ? 'bg-blue-100 text-blue-800' :
                        'bg-yellow-100 text-yellow-800'
                      }`}>
                         {episode.status}
                      </span>
                      {episode.error && (
                        <div className="text-xs text-red-600 mt-1" title={episode.error}>
                          Error occurred
                        </div>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {episode.publishedDate}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-blue-100 text-blue-800 border border-blue-200">
                        {episode.format}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 text-center">
                      {episode.preRolls}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 text-center">
                      {episode.midRolls}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900 text-center">
                      {episode.postRolls}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-400">
                      <button className="p-1 hover:text-gray-600">
                        <MoreHorizontal className="h-4 w-4" />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </PodPayLayout>
  );
}
