import React, { useState, useCallback } from 'react';
import { createEpisodes } from '../services/n8nApi';
import { toast } from 'react-toastify';
import { safeGetItem } from '../utils/localStorage';


export function useYouTubeSync() {
  const [state, setState] = useState({
    loading: false,
    error: null,
    channel: null,
    podcastId: null,
    videos: [],
    episodes: [],
    progress: 0,
    total: 0,
    done: false,
    status: '',
    selectedVideos: [],
  });

  // Load data from localStorage on mount
  React.useEffect(() => {
    const channelData = safeGetItem('channelData');
    const videosData = safeGetItem('videosData', []);
    const podcastId = localStorage.getItem('podcastId');

    if (channelData || videosData?.length > 0 || podcastId) {
      setState(s => ({
        ...s,
        channel: channelData,
        videos: videosData,
        podcastId: podcastId || null
      }));
    }
  }, []);

  // Helper: is YouTube connected?
  const isYouTubeConnected = !!(state.channel && state.channel.id);

  // Connect YouTube data is now handled by n8n in GoogleAuth
  // This hook now focuses on episode management
  const connectYouTube = useCallback(async () => {
    // Data should already be loaded from localStorage
    const channel = safeGetItem('channelData');
    const videos = safeGetItem('videosData', []);
    const podcastId = localStorage.getItem('podcastId');

    setState(s => ({ ...s, channel, videos, podcastId, loading: false }));

    return { channel, videos, podcastId, success: true };
  }, []);

  // Create episodes using n8n workflow
  const syncToMegaphone = useCallback(async (videos) => {
    if (!videos || videos.length === 0) {
      toast.warning('No videos to sync');
      return;
    }

    setState(s => ({ ...s, loading: true, progress: 0, total: videos.length, done: false, episodes: [], status: 'Creating podcast episodes...' }));
    const podcastId = state.podcastId || localStorage.getItem('podcastId') || '';
    const userInfo = safeGetItem('user_info', {});

    if (!podcastId) {
      setState(s => ({ ...s, error: 'No podcast ID found. Please reconnect your YouTube channel.', loading: false }));
      return;
    }

    try {
      // Extract video IDs for n8n workflow
      const videoIds = videos.map(v => v.id || v.videoId).filter(Boolean);

      // Call n8n episode creation workflow
      const result = await createEpisodes(podcastId, videoIds, userInfo.id);

      if (result.success) {
        const { successful, failed, total } = result.summary;

        // Update state with episode results
        const episodes = result.results.map(r => ({
          id: r.videoId,
          title: r.title,
          publishedAt: new Date().toISOString(),
          mp4Url: `https://youtube.com/watch?v=${r.videoId}`,
          publishedToMegaphone: r.status === 'created',
          error: r.status === 'failed' ? 'Episode creation failed' : undefined
        }));

        setState(s => ({ 
          ...s, 
          episodes, 
          progress: total, 
          total, 
          done: true, 
          loading: false, 
          status: `Sync complete! ${successful} successful, ${failed} failed` 
        }));

      } else {
        throw new Error(result.error || 'Episode creation failed');
      }

    } catch (err) {
      console.error('Episode creation process failed:', err);
      setState(s => ({ ...s, error: err.message, loading: false, status: '' }));
      toast.error('Episode creation failed: ' + err.message);
    }
  }, [state.podcastId]);

  // Video selection methods
  const selectVideo = useCallback((videoId) => {
    setState(s => ({
      ...s,
      selectedVideos: [...(s.selectedVideos || []), videoId]
    }));
  }, []);

  const deselectVideo = useCallback((videoId) => {
    setState(s => ({
      ...s,
      selectedVideos: (s.selectedVideos || []).filter(id => id !== videoId)
    }));
  }, []);

  const createEpisodes = useCallback(() => {
    const selectedVideoObjects = state.videos.filter(v => state.selectedVideos.includes(v.id));
    syncToMegaphone(selectedVideoObjects);
  }, [state.videos, state.selectedVideos, syncToMegaphone]);

  // Refresh data from localStorage
  const refreshData = useCallback(() => {
    const channel = safeGetItem('channelData');
    const videos = safeGetItem('videosData', []);
    const podcastId = localStorage.getItem('podcastId');

    setState(s => ({
      ...s,
      channel,
      videos,
      podcastId: podcastId || null
    }));
  }, []);

  return {
    ...state,
    connectYouTube,
    syncToMegaphone,
    isYouTubeConnected,
    refreshData,
    selectVideo,
    deselectVideo,
    createEpisodes,
  };
}
