import { useState, useEffect } from 'react';
import { fetchEpisodes } from '../services/n8nApi';
import { safeGetItem } from '../utils/localStorage';

export function useEpisodes(podcastId) {
  const [state, setState] = useState({
    loading: false,
    error: null,
    episodes: [],
    totalCount: 0
  });

  useEffect(() => {
    if (podcastId) {
      loadEpisodes();
    }
  }, [podcastId]);

  const loadEpisodes = async () => {
    try {
      setState(s => ({ ...s, loading: true, error: null }));

      const userInfo = safeGetItem('user_info', {});
      if (!userInfo.id) {
        throw new Error('User not authenticated');
      }

      console.log('ðŸ“» Loading episodes for podcast:', podcastId);
      const result = await fetchEpisodes(podcastId, userInfo.id);

      if (result.success) {
        setState(s => ({ 
          ...s, 
          episodes: result.episodes || [], 
          totalCount: result.totalCount || 0,
          loading: false 
        }));
        console.log(`âœ… Loaded ${result.episodes?.length || 0} episodes`);
      } else {
        throw new Error(result.error || 'Failed to load episodes');
      }
    } catch (error) {
      console.error('âŒ Failed to load episodes:', error);
      setState(s => ({ 
        ...s, 
        error: error.message, 
        loading: false,
        episodes: [],
        totalCount: 0
      }));
    }
  };

  const refreshEpisodes = () => {
    if (podcastId) {
      loadEpisodes();
    }
  };

  return {
    ...state,
    refreshEpisodes
  };
}
